define(['require', 'jquery', 'oae.core'], function(require, $, oae) {

    return function(uid, showSettings, widgetData) {

        // Cache the widget container
        var $rootel = $('#' + uid);


        ////////////
        // RENDER //
        ////////////

        var renderDefault = function() {
            oae.api.util.template().render($('#hello-template', $rootel), widgetData, $('#hello-container', $rootel));
            bindColors();
        };

        ////////////////////
        // EVENT BINDINGS //
        ////////////////////

        var bindColors = function() {
            $('.hello-color', $rootel).on('click', function(eventObj) {
                var colorId = $(eventObj.target).attr('data-colorId');
                var colorHex = $(eventObj.target).attr('data-colorHex');
                widgetData.color = {'colorId': colorId, 'hex': colorHex};
                widgetData.translatedColor = colorI18n(colorId);
                $.ajax({
                    'url': '/api/favorite/' + colorId,
                    'type': 'POST',
                    'success': renderDefault
                });
            });
        };

        ///////////////////////////
        // GET DATA FROM BACKEND //
        ///////////////////////////

        var fetchData = function(callback) {
            getColors(function() {
                getFavorite(callback);
            });
        };

        var getColors = function(callback) {
            $.ajax({
                'url': '/api/favorite/list',
                'success': function(data) {
                    widgetData.colors = data;
                    callback();
                }
            });
        };

        var getFavorite = function(callback) {
            $.ajax({
                'url': '/api/favorite',
                'success': function(data) {
                    if (!data.colorId) {
                        data = {'colorId': 'black', 'hex': '000000'};
                    }
                    widgetData.color = data;
                    widgetData.translatedColor = colorI18n(data.colorId);
                    callback();
                }
            });
        };

        //////////
        // UTIL //
        //////////

        var colorI18n = function(colorId) {
            return oae.api.i18n.translate('__MSG__' + colorId.toUpperCase() + '__', 'hello');
        };

        ////////////////////
        // INITIALISATION //
        ////////////////////

        var init = function() {
            widgetData = widgetData || {};
            fetchData(renderDefault);
        };

        init();
    };
});
